import { useNavigate } from "react-router-dom";
import { Info, Users, Rocket, Star } from "lucide-react";

const AboutPage = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col items-center bg-gradient-to-br from-sky-100 to-indigo-200 px-6 py-16">
      <h2 className="text-5xl font-extrabold mb-12 text-gray-800 drop-shadow-md text-center">
        Despre Noi
      </h2>

      <div className="bg-white/90 p-10 rounded-3xl shadow-xl w-full max-w-4xl border border-gray-200 backdrop-blur-md space-y-12">
        <Section
          icon={<Rocket className="text-indigo-600 w-6 h-6" />}
          title="Cum a început totul"
          paragraphs={[
            "Proiectul nostru a pornit din dorința unei echipe mici de a schimba modul în care oamenii trăiesc turismul.",
            "În 2020, am decis să transformăm ideea într-o realitate, oferind nu doar servicii, ci aventuri memorabile."
          ]}
        />
        <Section
          icon={<Star className="text-yellow-500 w-6 h-6" />}
          title="Cum ne facem remarcați"
          paragraphs={[
            "Ne-am construit reputația prin muncă, inovație și grijă constantă față de client.",
            "Ne diferențiem prin profesionalism, prețuri echilibrate și flexibilitate adaptată fiecărui tip de călător."
          ]}
        />
        <Section
          icon={<Users className="text-green-600 w-6 h-6" />}
          title="Echipa Noastră"
          paragraphs={[
            "Suntem o echipă diversă, pasionată de turism și tehnologie.",
            "Fiecare membru contribuie cu expertiză unică – de la design UI, la relații cu clienții – pentru a crea o experiență completă."
          ]}
        />
        <Section
          icon={<Info className="text-blue-500 w-6 h-6" />}
          title="De ce să alegi serviciile noastre?"
          paragraphs={[
            "Pentru că oferim mai mult decât o vacanță – oferim povești.",
            "Personalizăm fiecare experiență, respectăm bugetul tău și suntem alături de tine în fiecare etapă a călătoriei."
          ]}
        />
      </div>

      <button
        onClick={() => navigate("/")}
        className="mt-14 bg-indigo-600 text-white text-lg font-semibold py-3 px-8 rounded-full hover:bg-indigo-700 transition-all duration-300 shadow-lg"
      >
        ⬅ Înapoi la Home
      </button>
    </div>
  );
};

const Section = ({
  icon,
  title,
  paragraphs,
}: {
  icon?: React.ReactNode;
  title: string;
  paragraphs: string[];
}) => (
  <section className="space-y-4 max-w-prose mx-auto">
    <div className="flex items-center gap-3">
      {icon}
      <h3 className="text-2xl font-bold text-gray-900">{title}</h3>
    </div>
    {paragraphs.map((text, index) => (
      <p key={index} className="text-gray-700 leading-relaxed text-base">
        {text}
      </p>
    ))}
  </section>
);

export default AboutPage;
