import { useNavigate, useLocation } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name:             "Acasă", path: "/" },
    { name: "Caută", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  return (
    <nav className="bg-blue-700 py-2 shadow-md">
      {/* Logo pe stânga */}
      <div className="w-full px-6 flex items-center">
        <h1 className="text-2xl font-bold text-white">✈️ AirSwift</h1>
      </div>

      {/* Butoane centrate sub logo */}
      <div className="w-full flex justify-center mt-2 flex-wrap gap-2">
        {pages.map(({ name, path }) => (
          <button
            key={path}
            onClick={() => navigate(path)}
            className={`px-4 py-2 rounded-md font-medium transition
              ${
                location.pathname === path
                  ? "bg-white text-blue-700"
                  : "bg-blue-600 text-white hover:bg-white hover:text-blue-700"
              }`}
          >
            {name}
          </button>
        ))}
      </div>
    </nav>
  );
};

export default Navbar;
