import React, { useState } from 'react';

// Simbolurile din slot
const SYMBOLS = ['ðŸ’', 'ðŸ‹', 'ðŸ‡', 'ðŸ””', 'ðŸ‰', 'â­', 'ðŸ‘‘', '7ï¸âƒ£'];

// Generare simbol aleator
const getRandomSymbol = () => SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)];

// Dimensiuni role
const REELS = 5;
const ROWS = 3;

const SlotMachine: React.FC = () => {
  // Starea rolelor: matrice 5 coloane x 3 rÃ¢nduri simboluri
  const [reels, setReels] = useState<string[][]>(
    Array.from({ length: REELS }, () => Array.from({ length: ROWS }, getRandomSymbol))
  );

  // Starea pentru animatie "spinning"
  const [spinning, setSpinning] = useState(false);

  const spin = () => {
    if (spinning) return; // prevenim spam click

    setSpinning(true);

    // SimulÄƒm "Ã®nvaÌ‚rtirea" cu delay-uri
    let spinsCount = 0;
    const maxSpins = 10;

    const interval = setInterval(() => {
      setReels(
        Array.from({ length: REELS }, () => Array.from({ length: ROWS }, getRandomSymbol))
      );

      spinsCount++;
      if (spinsCount >= maxSpins) {
        clearInterval(interval);
        setSpinning(false);
      }
    }, 150); // actualizare simboluri la fiecare 150ms
  };

  return (
    <div style={{ textAlign: 'center', marginTop: 50, color: '#fff', fontFamily: 'Arial' }}>
      <h1>ðŸŽ° Slot Machine - Shiny Crown Style ðŸŽ°</h1>
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          gap: 12,
          marginBottom: 30,
          backgroundColor: '#222',
          padding: 20,
          borderRadius: 12,
          boxShadow: '0 0 15px gold',
          width: 'fit-content',
          marginLeft: 'auto',
          marginRight: 'auto',
        }}
      >
        {reels.map((col, colIndex) => (
          <div
            key={colIndex}
            style={{
              display: 'flex',
              flexDirection: 'column',
              gap: 12,
              backgroundColor: '#111',
              padding: 10,
              borderRadius: 8,
              width: 70,
              boxShadow: 'inset 0 0 10px #facc15',
            }}
          >
            {col.map((symbol, rowIndex) => (
              <div
                key={rowIndex}
                style={{
                  width: 60,
                  height: 60,
                  fontSize: 38,
                  backgroundColor: '#000',
                  color: 'gold',
                  borderRadius: 6,
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  boxShadow: 'inset 0 0 8px rgba(255,255,255,0.5)',
                  userSelect: 'none',
                }}
              >
                {symbol}
              </div>
            ))}
          </div>
        ))}
      </div>
      <button
        onClick={spin}
        disabled={spinning}
        style={{
          padding: '12px 28px',
          fontSize: 18,
          fontWeight: 'bold',
          backgroundColor: spinning ? '#444' : '#10b981',
          border: 'none',
          borderRadius: 8,
          color: 'white',
          cursor: spinning ? 'not-allowed' : 'pointer',
          boxShadow: spinning ? 'none' : '0 0 10px #10b981',
          transition: 'background-color 0.3s',
        }}
      >
        {spinning ? 'Se Ã®nvÃ¢rte...' : 'RuleazÄƒ roata! ðŸŽ°'}
      </button>
    </div>
  );
};

export default SlotMachine;
