import React, { useState } from "react";

const CheckInPage = () => {
  const [flightNumber, setFlightNumber] = useState("");
  const [passportNumber, setPassportNumber] = useState("");
  const [luggageOption, setLuggageOption] = useState("");
  const [checkInStatus, setCheckInStatus] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const handleCheckIn = async (e: React.FormEvent) => {
  e.preventDefault();

  const clientId = localStorage.getItem("idClient");

  // Verificare dacă toate câmpurile sunt goale
  if (!flightNumber && !passportNumber && !luggageOption) {
    setCheckInStatus("⚠️ Te rugăm să completezi numărul zborului, pașaportul și să selectezi tipul de bagaj.");
    return;
  }

  // Validări individuale
  if (!flightNumber) {
    setCheckInStatus("⚠️ Te rugăm să introduci numărul zborului.");
    return;
  }

  if (!passportNumber) {
    setCheckInStatus("⚠️ Te rugăm să introduci numărul pașaportului.");
    return;
  }

  if (!luggageOption) {
    setCheckInStatus("⚠️ Te rugăm să selectezi tipul de bagaj.");
    return;
  }

  if (!clientId) {
    setCheckInStatus("❌ Pentru a face check-in, te rugăm să te autentifici.");
    return;
  }

  setLoading(true);
  setCheckInStatus(null);

  try {
    const response = await fetch("https://localhost:7239/api/CheckIn", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        IDZbor: parseInt(flightNumber),
        IDClient: parseInt(clientId),
        NrPasaport: passportNumber,
        TipBagaj: luggageOption,
        CheckedIN: true,
      }),
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(errorText);
    }

    const detaliiResponse = await fetch(
      `https://localhost:7239/api/CheckIn/detalii/${flightNumber}/${clientId}`
    );

    if (!detaliiResponse.ok) {
      setCheckInStatus(
        "✅ Check-in efectuat cu succes, însă nu am putut prelua detaliile zborului."
      );
    } else {
      const data = await detaliiResponse.json();
      setCheckInStatus(
        `✅ Check-in efectuat! Bine ai venit, ${data.prenumeClient} ${data.numeClient}. Zborul tău: ${data.orasPlecare} → ${data.orasSosire}. Bagaj: ${data.tipBagaj}.`
      );
    }

    setFlightNumber("");
    setPassportNumber("");
    setLuggageOption("");
  } catch (error) {
    console.error(error);
    setCheckInStatus(
      "❌ Ne pare rău, a apărut o problemă la check-in. Te rugăm să încerci din nou mai târziu."
    );
  } finally {
    setLoading(false);
  }
};

  return (
    <div className="min-h-screen bg-gradient-to-tr from-blue-200 via-indigo-100 to-pink-100 flex justify-center items-center px-4 py-12">
      <div className="bg-white rounded-3xl shadow-2xl max-w-md w-full p-10">
        <h2 className="text-4xl font-bold mb-8 text-center text-indigo-700 drop-shadow-md">
          Check-in Zbor
        </h2>

        <form onSubmit={handleCheckIn} noValidate className="space-y-8">
          <div>
            <label
              htmlFor="flightNumber"
              className="block mb-2 text-lg font-semibold text-indigo-600"
            >
              Număr Zbor
            </label>
            <input
              type="number"
              id="flightNumber"
              value={flightNumber}
              onChange={(e) => setFlightNumber(e.target.value)}
              placeholder="Ex: 1234"
              required
              className="w-full rounded-xl border border-indigo-300 px-5 py-3 text-gray-700 placeholder-indigo-400
                         focus:outline-none focus:ring-4 focus:ring-indigo-400 focus:border-indigo-600
                         transition duration-300"
            />
          </div>

          <div>
            <label
              htmlFor="passportNumber"
              className="block mb-2 text-lg font-semibold text-indigo-600"
            >
              Număr Pașaport
            </label>
            <input
              type="text"
              id="passportNumber"
              value={passportNumber}
              onChange={(e) => setPassportNumber(e.target.value)}
              placeholder="Ex: AB1234567"
              required
              className="w-full rounded-xl border border-indigo-300 px-5 py-3 text-gray-700 placeholder-indigo-400
                         focus:outline-none focus:ring-4 focus:ring-indigo-400 focus:border-indigo-600
                         transition duration-300"
            />
          </div>

          <div>
            <label
              htmlFor="luggage"
              className="block mb-2 text-lg font-semibold text-indigo-600"
            >
              Tip Bagaj
            </label>
            <select
              id="luggage"
              value={luggageOption}
              onChange={(e) => setLuggageOption(e.target.value)}
              required
              className="w-full rounded-xl border border-indigo-300 px-5 py-3 text-gray-700
                         focus:outline-none focus:ring-4 focus:ring-indigo-400 focus:border-indigo-600
                         transition duration-300"
            >
              <option value="" disabled>
                Selectează opțiunea
              </option>
              <option value="Bagaj de cabină">Bagaj de cabină (max. 8kg)</option>
              <option value="Bagaj de cală">Bagaj de cală (max. 23kg)</option>
              <option value="Bagaj suplimentar">Bagaj suplimentar</option>
            </select>
          </div>

          <button
            type="submit"
            disabled={loading}
            className={`w-full ${
              loading ? "bg-indigo-400 cursor-not-allowed" : "bg-indigo-600 hover:bg-indigo-700"
            } text-white font-bold py-4 rounded-2xl shadow-lg
                       transition duration-300 ease-in-out focus:outline-none focus:ring-4
                       focus:ring-indigo-400 flex justify-center items-center space-x-3`}
          >
            {loading ? (
              <span>Se procesează...</span>
            ) : (
              <>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-6 w-6"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  strokeWidth={2}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M7 16v-4a4 4 0 014-4h8"
                  />
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M7 16l-4-4m0 0l4-4m-4 4h18"
                  />
                </svg>
                <span>Check-in</span>
              </>
            )}
          </button>
        </form>

        {checkInStatus && (
          <div
            className={`mt-8 text-center font-semibold text-lg ${
              checkInStatus.startsWith("✅")
                ? "text-green-700"
                : checkInStatus.startsWith("⚠️")
                ? "text-yellow-700"
                : "text-red-700"
            }`}
          >
            {checkInStatus}
          </div>
        )}
      </div>
    </div>
  );
};

export default CheckInPage;
