import React, { useState, ChangeEvent } from 'react';

interface User {
  id: number;
  username: string;
  email: string;
  sold: number;
  avatarUrl?: string;
}

const predefinedAvatars = [
  { id: 'user1', url: '/images/user1.png' },
  { id: 'user2', url: '/images/user2.png' },
  { id: 'user3', url: '/images/user3.png' },
  { id: 'user4', url: '/images/user4.png' },
];

const dummyUser: User = {
  id: 1,
  username: 'alex23',
  email: 'alex23@example.com',
  sold: 250.75,
  avatarUrl: predefinedAvatars[0].url, // avatar default primul din lista
};

const Profile: React.FC = () => {
  const [user, setUser] = useState<User>(dummyUser);
  const [uploadError, setUploadError] = useState<string | null>(null);

  const handleLogout = () => {
    alert('Logout realizat.');
    // Aici poți pune redirect sau altă logică
  };

  const handleAvatarSelect = (url: string) => {
    setUser(prev => ({ ...prev, avatarUrl: url }));
    setUploadError(null);
  };

  const handleFileUpload = (e: ChangeEvent<HTMLInputElement>) => {
    setUploadError(null);
    const file = e.target.files?.[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      setUploadError('Te rugăm să încarci un fișier imagine valid.');
      return;
    }

    if (file.size > 2 * 1024 * 1024) {
      setUploadError('Fișierul este prea mare. Maxim 2MB.');
      return;
    }

    const reader = new FileReader();
    reader.onload = () => {
      if (typeof reader.result === 'string') {
        setUser(prev => ({ ...prev, avatarUrl: reader.result as string }));
      }
    };
    reader.readAsDataURL(file);
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundImage: "url('/images/background-casino.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
        paddingTop: '4rem',
        paddingBottom: '4rem',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: '#374151',
      }}
    >
      <div
        style={{
          maxWidth: '480px',
          width: '100%',
          backgroundColor: 'rgba(255, 255, 255, 0.35)',
          padding: '2.5rem 2rem',
          borderRadius: '20px',
          boxShadow: '0 20px 40px rgba(0,0,0,0.12)',
          backdropFilter: 'blur(10px)',
          WebkitBackdropFilter: 'blur(10px)',
          userSelect: 'none',
          textAlign: 'center',
          transition: 'box-shadow 0.3s ease',
        }}
        onMouseEnter={e => {
          (e.currentTarget as HTMLDivElement).style.boxShadow = '0 25px 50px rgba(0,0,0,0.2)';
        }}
        onMouseLeave={e => {
          (e.currentTarget as HTMLDivElement).style.boxShadow = '0 20px 40px rgba(0,0,0,0.12)';
        }}
      >
        {/* Avatar */}
        <img
          src={user.avatarUrl}
          alt={`${user.username} avatar`}
          style={{
            width: 120,
            height: 120,
            borderRadius: '50%',
            marginBottom: '1.5rem',
            boxShadow: '0 5px 15px rgba(0,0,0,0.15)',
            objectFit: 'cover',
            userSelect: 'none',
          }}
          draggable={false}
        />

        {/* Avatar Selection */}
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '1rem',
            marginBottom: '1.5rem',
            flexWrap: 'wrap',
          }}
        >
          {predefinedAvatars.map(avatar => (
            <button
              key={avatar.id}
              onClick={() => handleAvatarSelect(avatar.url)}
              aria-label={`Select avatar ${avatar.id}`}
              style={{
                border:
                  user.avatarUrl === avatar.url
                    ? '3px solid #059669'
                    : '2px solid transparent',
                borderRadius: '50%',
                padding: 0,
                cursor: 'pointer',
                transition: 'border-color 0.3s',
                width: 60,
                height: 60,
                backgroundImage: `url(${avatar.url})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                boxShadow:
                  user.avatarUrl === avatar.url
                    ? '0 0 8px #10b981'
                    : '0 2px 6px rgba(0,0,0,0.1)',
              }}
            />
          ))}
        </div>

        {/* Upload custom avatar */}
        <label
          htmlFor="avatar-upload"
          style={{
            display: 'inline-block',
            backgroundColor: '#059669',
            color: 'white',
            padding: '0.6rem 1.2rem',
            borderRadius: '10px',
            cursor: 'pointer',
            fontWeight: '600',
            fontSize: '1rem',
            userSelect: 'none',
            marginBottom: '1rem',
          }}
        >
          Încarcă o poză
          <input
            id="avatar-upload"
            type="file"
            accept="image/*"
            onChange={handleFileUpload}
            style={{ display: 'none' }}
          />
        </label>

        {uploadError && (
          <p
            style={{
              color: '#b91c1c',
              fontWeight: '600',
              marginBottom: '1rem',
              userSelect: 'text',
            }}
          >
            {uploadError}
          </p>
        )}

        {/* Title */}
        <h1
          style={{
            fontSize: '2.25rem',
            fontWeight: '700',
            marginBottom: '2rem',
            color: '#059669',
            userSelect: 'text',
          }}
        >
          👤 Profil Utilizator
        </h1>

        {/* Info */}
        <div style={{ textAlign: 'left', userSelect: 'text' }}>
          <ProfileField label="Username" value={user.username} icon="👤" />
          <ProfileField label="Email" value={user.email} icon="✉️" />
          <ProfileField
            label="Sold cont"
            value={`${user.sold.toLocaleString('ro-RO', { minimumFractionDigits: 2, maximumFractionDigits: 2 })} RON`}
            icon="💰"
            highlight
          />
        </div>

        {/* Logout Button */}
        <button
          onClick={handleLogout}
          style={{
            marginTop: '2.5rem',
            width: '100%',
            backgroundColor: '#ef4444',
            color: 'white',
            padding: '0.9rem',
            border: 'none',
            borderRadius: '14px',
            cursor: 'pointer',
            fontWeight: '700',
            fontSize: '1.15rem',
            transition: 'background-color 0.3s ease, transform 0.1s ease',
            userSelect: 'none',
            boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
          }}
          onMouseEnter={e => (e.currentTarget.style.backgroundColor = '#b91c1c')}
          onMouseLeave={e => (e.currentTarget.style.backgroundColor = '#ef4444')}
          onMouseDown={e => (e.currentTarget.style.transform = 'scale(0.97)')}
          onMouseUp={e => (e.currentTarget.style.transform = 'scale(1)')}
          onMouseOut={e => (e.currentTarget.style.transform = 'scale(1)')}
        >
          Logout
        </button>
      </div>
    </div>
  );
};

interface ProfileFieldProps {
  label: string;
  value: string;
  icon?: string;
  highlight?: boolean;
}

const ProfileField: React.FC<ProfileFieldProps> = ({ label, value, icon, highlight }) => {
  return (
    <p
      style={{
        fontWeight: '600',
        fontSize: '1.1rem',
        marginBottom: '1.25rem',
        color: highlight ? '#065f46' : '#374151',
        display: 'flex',
        alignItems: 'center',
        gap: '0.75rem',
      }}
    >
      <span style={{ fontSize: '1.3rem' }}>{icon}</span>
      <strong style={{ minWidth: '100px', userSelect: 'none', color: '#059669' }}>{label}:</strong>
      <span style={{ fontWeight: '400' }}>{value}</span>
    </p>
  );
};

export default Profile;
