import React, { useState } from "react";

const CheckInPage = () => {
  const [flightNumber, setFlightNumber] = useState("");
  const [passportNumber, setPassportNumber] = useState("");
  const [luggageOption, setLuggageOption] = useState("");
  const [checkInStatus, setCheckInStatus] = useState<string | null>(null);
  const [errors, setErrors] = useState({
    flightNumber: false,
    passportNumber: false,
    luggageOption: false,
  });

  const handleCheckIn = async (e: React.FormEvent) => {
    e.preventDefault();

    setErrors({
      flightNumber: !flightNumber,
      passportNumber: !passportNumber,
      luggageOption: !luggageOption,
    });

    if (!flightNumber || !passportNumber || !luggageOption) {
      setCheckInStatus("Te rugăm să completezi toate câmpurile.");
      return;
    }

    const clientId = localStorage.getItem("idClient");
    const numeClient = localStorage.getItem("numeClient");
    const prenumeClient = localStorage.getItem("prenumeClient");

    if (!clientId) {
      setCheckInStatus("Trebuie să fii logat pentru check-in.");
      return;
    }

    try {
      const response = await fetch("https://localhost:7239/api/CheckIn", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          IDZbor: parseInt(flightNumber),
          IDClient: parseInt(clientId),
          NrPasaport: passportNumber,
          TipBagaj: luggageOption,
          CheckedIN: true,
        }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText);
      }

      setCheckInStatus(
        `Check-in reușit! ${prenumeClient} ${numeClient}, zborul tău a fost înregistrat.`
      );
    } catch (error) {
      console.error(error);
      setCheckInStatus("A apărut o eroare la check-in.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50 flex items-center justify-center p-6">
      <div className="bg-white backdrop-blur-md bg-opacity-70 rounded-xl shadow-2xl max-w-lg w-full p-10 border border-purple-200">
        <h2 className="text-4xl font-extrabold text-center text-indigo-700 mb-10 tracking-wide drop-shadow-sm">
          Check-in Zbor
        </h2>

        <form onSubmit={handleCheckIn} noValidate className="space-y-8">
          {/* Flight Number */}
          <div>
            <label
              htmlFor="flightNumber"
              className="block mb-2 text-lg font-medium text-indigo-900"
            >
              Număr Zbor
            </label>
            <input
              type="number"
              id="flightNumber"
              value={flightNumber}
              onChange={(e) => setFlightNumber(e.target.value)}
              placeholder="Ex: 1234"
              aria-invalid={errors.flightNumber ? "true" : "false"}
              aria-describedby="flightNumber-error"
              className={`w-full rounded-md border-2 px-4 py-3 text-indigo-900 placeholder-indigo-400 
                focus:outline-none focus:ring-4 focus:ring-indigo-300 transition 
                ${
                  errors.flightNumber
                    ? "border-red-500 focus:ring-red-400"
                    : "border-indigo-300"
                }`}
              required
            />
            {errors.flightNumber && (
              <p
                id="flightNumber-error"
                className="mt-2 text-sm text-red-600 font-semibold"
                role="alert"
              >
                Te rugăm să introduci numărul zborului.
              </p>
            )}
          </div>

          {/* Passport Number */}
          <div>
            <label
              htmlFor="passportNumber"
              className="block mb-2 text-lg font-medium text-indigo-900"
            >
              Număr Pașaport
            </label>
            <input
              type="text"
              id="passportNumber"
              value={passportNumber}
              onChange={(e) => setPassportNumber(e.target.value)}
              placeholder="Ex: AB1234567"
              aria-invalid={errors.passportNumber ? "true" : "false"}
              aria-describedby="passportNumber-error"
              className={`w-full rounded-md border-2 px-4 py-3 text-indigo-900 placeholder-indigo-400 
                focus:outline-none focus:ring-4 focus:ring-indigo-300 transition
                ${
                  errors.passportNumber
                    ? "border-red-500 focus:ring-red-400"
                    : "border-indigo-300"
                }`}
              required
            />
            {errors.passportNumber && (
              <p
                id="passportNumber-error"
                className="mt-2 text-sm text-red-600 font-semibold"
                role="alert"
              >
                Te rugăm să introduci numărul pașaportului.
              </p>
            )}
          </div>

          {/* Luggage Option */}
          <div>
            <label
              htmlFor="luggage"
              className="block mb-2 text-lg font-medium text-indigo-900"
            >
              Tip Bagaj
            </label>
            <select
              id="luggage"
              value={luggageOption}
              onChange={(e) => setLuggageOption(e.target.value)}
              aria-invalid={errors.luggageOption ? "true" : "false"}
              aria-describedby="luggage-error"
              className={`w-full rounded-md border-2 px-4 py-3 text-indigo-900 bg-white
                focus:outline-none focus:ring-4 focus:ring-indigo-300 transition
                ${
                  errors.luggageOption
                    ? "border-red-500 focus:ring-red-400"
                    : "border-indigo-300"
                }`}
              required
            >
              <option value="" disabled>
                Selectează tipul de bagaj
              </option>
              <option value="Bagaj de cabină">Bagaj de cabină (max. 8kg)</option>
              <option value="Bagaj de cală">Bagaj de cală (max. 23kg)</option>
              <option value="Bagaj suplimentar">Bagaj suplimentar</option>
            </select>
            {errors.luggageOption && (
              <p
                id="luggage-error"
                className="mt-2 text-sm text-red-600 font-semibold"
                role="alert"
              >
                Te rugăm să selectezi tipul de bagaj.
              </p>
            )}
          </div>

          <button
            type="submit"
            className="w-full py-4 bg-indigo-600 text-white font-bold rounded-xl shadow-lg hover:bg-indigo-700 active:scale-95 transform transition duration-150"
          >
            Check-in
          </button>
        </form>

        {checkInStatus && (
          <div
            className="mt-8 p-4 rounded-lg text-center font-semibold text-indigo-900 bg-indigo-100 shadow-inner"
            role="status"
          >
            {checkInStatus}
          </div>
        )}
      </div>
    </div>
  );
};

export default CheckInPage;
