import React, { useState } from 'react';

const Login: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Simulare login
    if (username === 'user' && password === 'password') {
      alert('Login reușit!');
      // Redirect sau setare context autentificare
    } else {
      setError('Username sau parola incorectă');
    }
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center flex items-center justify-center p-6"
      style={{ backgroundImage: "url('/images/background-casino.jpg')" }}
    >
      <div className="max-w-md w-full bg-black bg-opacity-70 p-6 rounded shadow-md text-white">
        <h1 className="text-3xl font-bold mb-6 text-center">Login</h1>
        <form onSubmit={handleSubmit}>
          <label className="block mb-4 font-semibold">
            Username
            <input
              type="text"
              className="w-full border border-gray-300 rounded px-3 py-2 mt-1 text-black"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </label>

          <label className="block mb-4 font-semibold">
            Parola
            <input
              type="password"
              className="w-full border border-gray-300 rounded px-3 py-2 mt-1 text-black"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </label>

          {error && <p className="text-red-500 mb-4">{error}</p>}

          <button
            type="submit"
            className="w-full bg-blue-700 hover:bg-blue-800 text-white py-2 rounded transition"
          >
            Loghează-te
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
