import { useEffect, useState } from "react";

interface PassengerProfile {
  fname: string;
  lname: string;
  email: string;
  phone: string;
  address: string;
  parola_noua: string;
}

const ProfilePage = () => {
  const [profile, setProfile] = useState<PassengerProfile | null>(null);
  const [editableProfile, setEditableProfile] =
    useState<PassengerProfile | null>(null);

  useEffect(() => {
    // Înlocuiește cu apel real către API
    const fakeUser: PassengerProfile = {
      fname: "Andrei",
      lname: "Popescu",
      email: "andrei@example.com",
      phone: "0712345678",
      address: "București, România",
      parola_noua: "1234",
    };
    setProfile(fakeUser);
    setEditableProfile(fakeUser);
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (editableProfile) {
      setEditableProfile({
        ...editableProfile,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleSave = () => {
    if (editableProfile) {
      // Poți salva datele (de exemplu, trimite-le la server)
      setProfile(editableProfile);
      alert("Datele au fost salvate!");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 px-4">
      <h2 className="text-3xl font-bold mb-6">Profilul Meu</h2>
      {editableProfile && (
        <div className="bg-white p-6 rounded-xl shadow max-w-md w-full space-y-4">
          <div>
            <label>Prenume:</label>
            <input
              type="text"
              name="fname"
              value={editableProfile.fname}
              onChange={handleChange}
              className="w-full p-3 border rounded"
            />
          </div>
          <div>
            <label>Nume:</label>
            <input
              type="text"
              name="lname"
              value={editableProfile.lname}
              onChange={handleChange}
              className="w-full p-3 border rounded"
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={editableProfile.email}
              onChange={handleChange}
              className="w-full p-3 border rounded"
            />
          </div>
          <div>
            <label>Telefon:</label>
            <input
              type="text"
              name="phone"
              value={editableProfile.phone}
              onChange={handleChange}
              className="w-full p-3 border rounded"
            />
          </div>
          <div>
            <label>Adresă:</label>
            <input
              type="text"
              name="address"
              value={editableProfile.address}
              onChange={handleChange}
              className="w-full p-3 border rounded"
            />
          </div>
          <div>
            <label>Parola_noua:</label>
            <input
              type="text"
              name="parola noua"
              value={editableProfile.parola_noua}
              onChange={handleChange}
              className="w-full p-3 border rounded"
            />
          </div>
          {/* Buton pentru a salva modificările */}
          <button
            onClick={handleSave}
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
          >
            Salvează Modificările
          </button>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
