import React from 'react';

const Home: React.FC = () => {
  return (
    <div 
      className="flex flex-col items-center justify-center text-white bg-cover bg-center" 
      style={{ 
        backgroundImage: "url('/images/background-casino.jpg')", 
        height: '100vh',   // Forțăm înălțimea la 100% viewport height
        width: '100vw',    // Forțăm lățimea la 100% viewport width
        backgroundRepeat: 'no-repeat',  // să nu repete imaginea
        backgroundSize: 'cover',         // să acopere tot containerul
        backgroundPosition: 'center center' // să fie centrată
      }}
    >
      <header className="mb-10 flex items-center space-x-4">
        <img 
          src="/images/casino-logo192.png" 
          alt="Casino Logo" 
          style={{ width: '240px', height: '240px', borderRadius: '0.375rem' }} 
        />
        <h1 className="text-5xl font-bold drop-shadow-lg">Bun venit la Casino Online</h1>
      </header>

      <p className="text-lg max-w-xl text-center drop-shadow-md">
        Joacă cele mai tari jocuri de cazino: Blackjack, Poker, Ruletă, Slot Machines și multe altele!
      </p>

      <div className="mt-12 flex space-x-6">
        <img 
          src="/images/blackjack.jpg" 
          alt="Blackjack" 
          style={{ width: '240px', height: '180px' }} 
          className="rounded shadow-lg hover:scale-105 transition-transform cursor-pointer" 
        />
        <img 
          src="/images/poker-table.jpg" 
          alt="Poker" 
          style={{ width: '240px', height: '180px' }} 
          className="rounded shadow-lg hover:scale-105 transition-transform cursor-pointer" 
        />
        <img 
          src="/images/roulette-wheel.jpg" 
          alt="Ruletă" 
          style={{ width: '240px', height: '180px' }} 
          className="rounded shadow-lg hover:scale-105 transition-transform cursor-pointer" 
        />
        <img 
          src="/images/slot-machine.jpg" 
          alt="Slot Machine" 
          style={{ width: '240px', height: '180px' }} 
          className="rounded shadow-lg hover:scale-105 transition-transform cursor-pointer" 
        />
      </div>
    </div>
  );
};

export default Home;
export {};
